// Import global API
import Api from '../../api/Api'

const category = {

    // Set namespace
    namespaced: true,

    // State
    state: {

        // Index categories
        categories: [],

    },

    // Mutations
    mutations: {

        // Set state categories dengan data dari response
        GET_CATEGORIES(state, categories) {
            state.categories = categories
        },

    },

    // Actions
    actions: {

        // Action getCategories

    },

    // Getters
    getters: {

    }
}

export default category
