// Import global API
import Api from '../../api/Api'

const slider = {

    // Set namespace
    namespaced: true,

    // State
    state: {

        // Index slider
        slider: [],

    },

    // Mutations
    mutations: {

        // Set state sliders dengan data dari response
        GET_SLIDERS(state, sliders) {
            state.sliders = sliders
        }

    },

    // Actions
    actions: {

    },

    // Getters
    getters: {

    }
}

export default slider
