import Api from '../../api/Api'

const product = {

    // Set namespace
    namespaced: true,

    // State
    state: {
        products: [],
    },

    // Mutations
    mutations: {

        // Set state products dengan data dari response
        GET_PRODUCTS(state, products) {
            state.products = products
        },
    },

    // Actions
    actions: {

        // Action getProducts
        getProducts({ commit }) {

            // Get data sliders ke server
            Api.get('/product')
        }
    },

    // Getters
    getters: {

    }
}

export default product
