// Import global API
import Api from '../../api/Api'

const cart = {
    
    //set namespace true
    namespaced: true,
    
    //state
    state: {

        // Cart
        cart: [],

        // Total Cart
        cartTotal: 0,
    
    },
    
    //mutations
    mutations: {

        // Get data cart
        GET_CART(state, product) {
            state.cart = product
        },

        // Get total cart
        TOTAL_CART(state, total) {
            state.cartTotal = total
        },

        // Get cart weight
        CART_WEIGHT(state, weight) {
            state.cartWeight = weight
        },
    
    },
    
    //actions
    actions: {

        // Action addToCart
        addToCart({ commit }, { product_id, price, quantity, weight }) {

            // Get data token user
            const token = localStorage.getItem('token')
            const user = JSON.parse(localStorage.getItem('user'))

            // Set Axios header dengan type Authorization + Bearer Token
            Api.defaults.headers.common['Authorization'] = "Bearer " + token

            // Send data Cart ke server
            Api.post('/cart', {
                product_id: product_id,
                price: price,
                quantity: quantity,
                weight: weight,
                customer_id: user.id
            })
            .then(() => {

                // Get data Cart
                Api.get('/cart')
                .then(response => {

                    // Commitmutation GET_CART
                    commit('GET_CART', response.data.cart)
                })

                // Get total Cart
                Api.get('/cart/total')
                .then(response => {

                    // Commit mutation TOTAL_CART
                    commit('TOTAL_CART', response.data.total)
                })
            })
        },

        // Cart Count
        cartCount({ commit }) {

            // Get data token dan user
            const token = localStorage.getItem('token')

            // Set Axios header dengan type Authorization + Bearer token
            Api.defaults.header
        }
    
    },
    
    //getters
    getters: {

        // Get cart
        getCart(state) {
            return state.cart
        },

        // Count Cart
        cartCount(state) {
            return state.cart.length
        }
    
    }
   
}

export default cart
