// Import global API
import Api from '../../api/Api'

const cart = {
    
    //set namespace true
    namespaced: true,
    
    //state
    state: {

        // Cart
        cart: [],

        // Total Cart
        cartTotal: 0,
    
    },
    
    //mutations
    mutations: {

        // Get data cart
        GET_CART(state, product) {
            state.cart = product
        }
    
    },
    
    //actions
    actions: {
    
    },
    
    //getters
    getters: {
    
    }
   
}

export default cart
